---
extend: '@vue/cli-service/generator/template/src/App.vue'
replace:
- !!js/regexp /
<template>[^]*?<\/template>/
  - !!js/regexp /\n
  <script>[ ^
  ]*
      ? <\/script>\n/
  - !!js / regexp / margin - top[ ^
  ]*
      ? <\/style>/
  ---

      <%#
  REPLACE % >
  < template >
  < div
  id = "app" >
      < div
  id = "nav" >
      < router - link
  to = "/" > Home < /router-link> |
  <router-link to="/about">About</router-link>
  </div>
  <router-view/>
  </div>
  </template>
  <%#
  END_REPLACE % >

  < %
  #
  REPLACE % >
  < %
  #
  END_REPLACE % >

  < %
  #
  REPLACE % >
  }

  <%_
  if (rootOptions.cssPreprocessor !== 'stylus') {
    _ % >
    < % _
    if (!rootOptions.cssPreprocessor) {
      _ % >
      #nav
      {
        padding: 30
        px;
      }

      #nav
      a
      {
        font - weight
      :
        bold;
        color: #
        2
        c3e50;
      }

      #nav
      a.router - link - exact - active
      {
        color: #
        42
        b983;
      }
      <%_
    } else {
      _ % >
      #nav
      {
        padding: 30
        px;

        a
        {
          font - weight
        :
          bold;
          color: #
          2
          c3e50;

        &.
          router - link - exact - active
          {
            color: #
            42
            b983;
          }
        }
      }
      <%_
    }
    _ % >
    < % _
  } else {
    _ % >
    #nav
    padding
    30
    px
    a
    font - weight
    bold
    color
  #
    2
    c3e50
    &
  .
    router - link - exact - active
    color
  #
    42
    b983
    < % _
  }
  _ % >
  < /style>
  <%#
  END_REPLACE % >
